import React, { useEffect, useRef, useMemo } from 'react';
import './Cursor.css';

const Cursor = () => {
  const coords = useMemo(() => ({ x: 0, y: 0 }), []); // Memoize the initialization of 'coords'
  
  const colors = useMemo(() => [
    "#7c7979", "#7c7979", "#7c7979", "#7c7979", "#7c7979", "#7c7979", "#7c7979",
    "#7c7979", "#7c7979", "#7c7979", "#7c7979", "#7c7979", "#7c7979", "#7c7979",
    "#7c7979", "#7c7979", "#7c7979", "#7c7979", "#7c7979", "#7c7979", "#7c7979", "#7c7979"
  ], []); // Memoize the initialization of 'colors'

  const circlesRef = useRef(colors.map(() => useRef(null))); // Use useRef inside the component

  useEffect(() => {
    const circles = circlesRef.current;

    circles.forEach((circle, index) => {
      circle.current = { x: 0, y: 0 };
      circle.current.style.backgroundColor = colors[index % colors.length];
    });

    const handleMouseMove = (e) => {
      coords.x = e.clientX;
      coords.y = e.clientY;
    };

    window.addEventListener("mousemove", handleMouseMove);

    const animateCircles = () => {
      let x = coords.x;
      let y = coords.y;

      circles.forEach((circle, index) => {
        circle.current.style.left = x - 12 + "px";
        circle.current.style.top = y - 12 + "px";

        circle.current.style.transform = `scale(${(circles.length - index) / circles.length})`;

        circle.current.x = x;
        circle.current.y = y;

        const nextCircle = circles[index + 1] || circles[0];
        x += (nextCircle.current.x - x) * 0.3;
        y += (nextCircle.current.y - y) * 0.3;
      });

      requestAnimationFrame(animateCircles);
    };

    animateCircles();

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, [colors, coords]);

  return (
    <div>
      {colors.map((color, index) => (
        <div
          key={index}
          className="circle"
          ref={circlesRef.current[index]}
        ></div>
      ))}
    </div>
  );
};

export default Cursor;
