import React, { useState, useEffect } from "react";
import { useTheme } from "../context/ThemeContext";
import './header.css';
import { HiOutlineHome, HiOutlineUser, HiOutlineBadgeCheck, HiOutlineClipboardList, HiOutlinePhotograph, HiOutlineMail, HiX, HiOutlineMenu } from "react-icons/hi";

const Header = () => {
    const { theme } = useTheme(); // Access the current theme

    // Use state for menu visibility and active navigation
    const [toggle, setToggle] = useState(false);
    const [activeNav, setActiveNav] = useState("#home");

    // Update styles based on theme changes
    useEffect(() => {
        const header = document.querySelector(".header");
        if (theme === 'dark') {
            header.classList.add("dark-header");
        } else {
            header.classList.remove("dark-header");
        }
    }, [theme]);

    // Event listener for scroll
    useEffect(() => {
        const handleScroll = () => {
            const header = document.querySelector(".header");
            if (window.scrollY >= 80) {
                header.classList.add("scroll-header");
            } else {
                header.classList.remove("scroll-header");
            }
        };

        window.addEventListener("scroll", handleScroll);

        return () => {
            window.removeEventListener("scroll", handleScroll);
        };
    }, []);

    return (
        <header className={`header ${theme}`}>
            <nav className="nav container">
                <a href="index.html" className="nav__logo">Parvesh</a>
                <div className={toggle ? "nav__menu show-menu" : "nav__menu"}>
                    {/* ... existing code ... */}
                </div>
                <div className="nav__toggle" onClick={() => setToggle(!toggle)}>
                    <HiOutlineMenu />
                </div>
            </nav>
        </header>
    );
}

export default Header;
