import React, { useState } from 'react';
import './darklight.css';

const Theme = () => {
  const [isSun, setIsSun] = useState(true);

  const toggleTheme = () => {
    setIsSun((prev) => !prev);
  };

  return (
    <div className={`app ${isSun ? 'light' : 'dark'}`}>
      <div className="toggle-container" onClick={toggleTheme}>
        {isSun ? (
          <img src="/assets/sun_light.png" alt="Sun" className="theme-icon" />
        ) : (
          <img src="/assets/moon_light.png" alt="Moon" className="theme-icon" />
        )}
      </div>
      
    </div>
  );
};

export default Theme;
